package com.acadglid.java.core.session1.assignment4;

public class ExpressionEvaluation {
	static int x=5;
	static int y=10;

	public static void main(String[] args) {
		// TODO Auto-generated method stub
System.out.println("Evaluation of expression x+y*2 is "+(x+y*2));
System.out.println("Evaluation of expression x-y+2 is " +(x-y+2));
System.out.println("Evaluation of expression (x+y)*2 is "+((x+y)*2));
System.out.println("Evaluation of expression y%x is "+ y%x);
	}

}
